import React, { useState } from 'react';


const App = () => {

    const [num,setNum]=useState(0);

    const incNum=()=>{
        setNum(num+1);
    }
    const decNum=()=>{
        if(num>0){
        setNum(num-1)
        }
        else{
            alert('Sorry! You Hv Reached Zero Limit')
            setNum(0);

        }
    }
    


    return(
        <>
           <div className="main_div">
           <div className="child_div">
                <h1>{num}</h1>
                <div className="div_btn">
                <button onClick={incNum}><i class="fas fa-plus"></i></button>
                <button onClick={decNum}>Decrem</button>
                </div>
                </div>
           </div>
        </>
    );
                    
};

export  default App;
