import React, { useState } from 'react';


const App = () => {

    const lgreen="#8e44ad";
    const [bg,setBg]= new useState(lgreen);

    const bgChange=()=>{
        setBg(bg);
    };



    return (
        <>
        <div>
        <h1 > </h1>
        <button onClick={bgChange}>Click  Me</button>
        </div>
        </>
    );
};

export  default App;
