import React, { useState } from 'react';


const App = () => {


    const [name,setName]=useState("");

    const inputEvent=(event)=>{
                setName(event.target.value)
    }

    return (
        <>
        <div>
        <h1>Hello {name}</h1>
        <input type="text" 
               placeholder="Enter Your Name" 
               Value="" 
               onChange={inputEvent}
               onClick={inputEvent}
               />
        <button>Click Me 👍</button>
        </div>
        </>
    );
};

export  default App;
