import React, { useState } from "react";
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import ListCom from './ListComp';

const App =()=>{

    const [item,setItem]=new useState("");
    const [newItem,setNewItem]=new useState([]);

    const itemEvent =(event)=>{
          setItem(event.target.value);
          
    }
    const listOfItem=()=>{
        setNewItem((prevValue)=>{
            return [...prevValue,item]
        });
        setItem("");
    }

    return(
        <>
        <div className="main_div">
            <div className="center_div">
                <br />
                <h1> ToDo List</h1>
                <br />
                <input type="text" value={item} placeholder="Add an Item" onChange={itemEvent}/>
                <Button className="newBtn" onClick={listOfItem}><AddIcon /></Button>
                <br />
                <ol>
                    {newItem.map((val,index)=>{
                            return <ListCom key={index} text={val} />
                    })}
                </ol>
                <br />
            </div>
        </div>

        </>
    )
};
export default App;
