import React, { useState } from 'react';


const App = () => {

    const lgreen="#8e44ad";
    const [bg,setBg]= new useState(lgreen);

    const bgChange=()=>{
        let newBg="lightseagreen"
        setBg(newBg);
    };



    return (
        <>
        <div style={{backgroundColor:bg}}>
        <button onClick={bgChange}>Click  Me</button>
        </div>
        </>
    );
};

export  default App;
