import React, { useState } from 'react';


const App = () => {

    const lgreen="lightsalmon";
    const [bg,setBg]= new useState(lgreen);
    const [name,setName]= new useState("Click Me!");

    const bgChange=()=>{
        let newBg="lightseagreen"
        setBg(newBg);
        setName("Hurrah !! ðŸ˜‰");
    };



    return (
        <>
        <div style={{backgroundColor:bg}}>
        <button onClick={bgChange}>{name}</button>
        </div>
        </>
    );
};

export  default App;
